// function on
jQuery(document).ready(function() {
	initAccordion();
});
// function init
function initAccordion() {
	jQuery('.simple-accordion').slideAccordion({
		activeClass:'active',
		opener: '>a.opener',
		slider: '>div.slide',
		animSpeed: 300
	});
	jQuery('.multilevel-accordion').slideAccordion({
		opener: '>a.opener',
		slider: '>div.slide',
		animSpeed: 300,
		scrollToActiveItem: {
			enable: true,
			breakpoint: 1920, // max-width
			animSpeed: 600,
			extraOffset: null
		}
	});
}

