/* module simple-count */
// function on
jQuery(document).ready(function() {
	initViewportCheckerCount();
});

// function init
function initViewportCheckerCount(){
	jQuery('.count-s').viewportChecker({
			classToAdd: 'counter-s',
			offset: 10,
			callbackFunction: function(elem, action){
				countSimpleInit();
			}
	});
}

function countSimpleInit() {
	jQuery(".counter-s").each(function() {
		var $this = jQuery(this),
			countTo = $this.attr("data-count");
		jQuery({ countNum: $this.text() }).animate({
			countNum: countTo
		}, {
			duration: 2000,
			easing: "linear",
			step: function() {
				$this.text(Math.floor(this.countNum));
			},
			complete: function() {
				$this.text(this.countNum);
				//alert('finished');
			}
		});
	});
};

